import React, { useEffect, useState } from "react";
import axios from 'axios';
import { Add } from '@material-ui/icons';
import { Link, NavLink } from "react-router-dom";
import { Box, Fab, makeStyles, Table, TableRow, TableCell, TableContainer, TableHead, TableBody, Paper, Typography } from "@material-ui/core";
import { Card, CardHeader, CardContent, CardMedia, CardActions, Avatar, IconButton, Container } from "@material-ui/core";
import { Delete, Edit, MoreVert, Favorite, Share } from "@material-ui/icons";

export default function EventList() {
  // This is the constructor that shall store our data retrieved from the database
  const [events, setEvents] = useState([])

  // This method will get the data from the database.
  useEffect(() => {
    axios
      .get("http://localhost:5000/record/")
      .then((response) => {
        setEvents(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  // This method will delete an event based on the method
  const deleteEvent = (id) => {
    console.log("id: ", id)
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });
    setEvents(events.filter((el) => el._id !== id));
  }

  // This following section will display the table with the records of individuals.
  return (
    <Container maxWidth="sm">
      {/* <TableContainer component={Paper} className={classes.darkHead}>
        {records.map((currentrecord) => (
          <TableRow
            key={currentrecord._id}
          >
            <TableCell>{currentrecord.person_name}</TableCell>
            <TableCell>{currentrecord.person_position}</TableCell>
            <TableCell>{currentrecord.person_level}</TableCell>
            <TableCell align="right">
              <Link to={"/edit/" + currentrecord._id}>
                <Edit fontSize="small" style={{marginRight: 10}}/>
              </Link>
              <a
                href="/"
                onClick={() => {
                  deleteRecord(currentrecord._id);
                }}
              >
                <Delete fontSize="small"/>
              </a>
            </TableCell>
          </TableRow>
        ))}
      </TableContainer> */}
      <Card sx={{ maxWidth: 345 }}>
        <CardHeader
          avatar={
            <Avatar aria-label="recipe">
              A
            </Avatar>
          }
          action={
            <IconButton aria-label="settings">
              <MoreVert />
            </IconButton>
          }
          title="Boling with the boys"
          subheader="December 29, 2021"
        />
        <CardMedia
          component="img"
          height="194"
          src={"https://s3-media0.fl.yelpcdn.com/bphoto/4xekxff-5mjxc_9VaHA2hQ/o.jpg"}
          alt="Bowling"
        />
        <CardContent>
          <Typography variant="body2" color="secondary">
            We're going bowling at Ten Pin in Turlock before Andrew goes to NY
          </Typography>
        </CardContent>
        <CardActions disableSpacing>
          <IconButton aria-label="add to favorites">
            <Favorite />
          </IconButton>
          <IconButton aria-label="share">
            <Share />
          </IconButton>
        </CardActions>
      </Card>

      <NavLink to="/create">
        <Fab color="secondary" variant="extended" style={{position: 'relative', margin: 15}}> 
          <Add />
          Create Event
        </Fab>
      </NavLink>
    </Container>
  );
}
