import { Box, Fab, makeStyles, Table, TableRow, TableCell, TableContainer, TableHead, TableBody, Paper, Typography } from "@material-ui/core";
import { Card, CardHeader, CardContent, CardMedia, CardActions, Avatar, IconButton } from "@material-ui/core";
import { Delete, Edit, FavoriteIcon, ShareIcon, MoreVertIcon } from "@material-ui/icons";

export default function EventList() {
  // This is the constructor that shall store our data retrieved from the database
  const [events, setEvents] = useState([])

  // This method will get the data from the database.
  useEffect(() => {
    axios
      .get("http://localhost:5000/record/")
      .then((response) => {
        setEvents(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  // This method will delete an event based on the method
  const deleteEvent = (id) => {
    console.log("id: ", id)
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });
    setEvents(events.filter((el) => el._id !== id));
  }

  // This following section will display the table with the records of individuals.
  return (
