import React, { useEffect, useState } from "react";
// This will require to npm install axios
import axios from 'axios';
import { TextField } from "@material-ui/core";

function Create(props) {
  const [personName, setPersonName] = useState('');
  const [personPosition, setPersonPosition] = useState('');
  const [personLevel, setPersonLevel] = useState('');
  const [records, setRecords] = useState([]);

	// This function will handle the submission.
	const handleSubmit = (e) => {
    e.preventDefault();

    // When post request is sent to the create url, axios will add a new record(newperson) to the database.
    const newperson = {
      person_name: personName,
      person_position: personPosition,
      person_level: personLevel,
    };
		console.log(newperson);

    axios
      .post("http://localhost:5000/record/add", newperson)
      .then((res) => console.log(res.data));

    // We will empty the state after posting the data to the database
		setPersonName("");
		setPersonPosition("");
		setPersonLevel("");
  }

  // This following section will display the form that takes the input from the user.
    return (
      <div style={{ marginTop: 20 }}>
        <h3>Create New Record</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <TextField
              style={{color: "white", marginBottom: 15}}
              label="Name of the person:"
              variant="outlined"
              size="small"
              type="text"
              className="form-control"
              value={personName}
              onChange={e => setPersonName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <TextField
              style={{color: "white", marginBottom: 15}}
              label="Person's position:"
              variant="outlined"
              size="small"
              type="text"
              className="form-control"
              value={personPosition}
              onChange={e => setPersonPosition(e.target.value)}
            />
          </div>
          <div className="form-group">
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="priorityOptions"
                id="priorityLow"
                value="Intern"
                checked={personLevel === "Intern"}
                onChange={e => setPersonLevel(e.target.value)}
              />
              <label className="form-check-label">Intern</label>
            </div>
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="priorityOptions"
                id="priorityMedium"
                value="Junior"
                checked={personLevel === "Junior"}
                onChange={e => setPersonLevel(e.target.value)}
              />
              <label className="form-check-label">Junior</label>
            </div>
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="priorityOptions"
                id="priorityHigh"
                value="Senior"
                checked={personLevel === "Senior"}
                onChange={e => setPersonLevel(e.target.value)}
              />
              <label className="form-check-label">Senior</label>
            </div>
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Create person"
              className="btn btn-primary"
            />
          </div>
        </form>
      </div>
    );
}

export default Create;
