import React, { useEffect, useState } from "react";
// This will require to npm install axios
import axios from 'axios';
import { Link } from "react-router-dom";
import { Table, TableRow, TableCell, TableContainer, TableHead, TableBody, Paper } from "@material-ui/core";

const Record = (record, deleteRecord, key) => (
  <TableRow
    key={key}
    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
  >
    <TableCell align="right">{record.person_name}</TableCell>
    <TableCell align="right">{record.person_position}</TableCell>
    <TableCell align="right">{record.person_level}</TableCell>
    <TableCell align="right">
      <Link to={"/edit/" + key}>Edit</Link> |
      <a
        href="/"
        onClick={() => {
          deleteRecord(key);
        }}
      >
        Delete
      </a>
    </TableCell>
  </TableRow>
);

export default function RecordList(props) {
  // This is the constructor that shall store our data retrieved from the database
  const [records, setRecords] = useState([])

  // This method will get the data from the database.
  useEffect(() => {
    axios
      .get("http://localhost:5000/record/")
      .then((response) => {
        setRecords(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  // This method will delete a record based on the method
  const deleteRecord = (id) => {
    console.log("id: ", id)
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });
    setRecords(records.filter((el) => el._id !== id));
  }

  // This method will map out the users on the table
  const recordList = () => {
    return records.map((currentrecord) => {
      return (
        <Record
          record={currentrecord}
          deleteRecord={deleteRecord}
          key={currentrecord._id}
        />
      );
    });
  }

  // This following section will display the table with the records of individuals.
  return (
    //   <table className="table table-striped" style={{ marginTop: 20 }}>
    <TableContainer component={Paper}>
    <h3>Record List</h3>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell>Position</TableCell>
            <TableCell>Level</TableCell>
            <TableCell>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {recordList}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
