import React, { useEffect, useState } from "react";
// This will require to npm install axios
import axios from 'axios';
import { Link } from "react-router-dom";
import { Table, TableRow, TableCell, TableContainer, TableHead, TableBody, Paper, Typography } from "@material-ui/core";

export default function RecordList() {
  // This is the constructor that shall store our data retrieved from the database
  const [records, setRecords] = useState([])

  // This method will get the data from the database.
  useEffect(() => {
    axios
      .get("http://localhost:5000/record/")
      .then((response) => {
        setRecords(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  // This method will delete a record based on the method
  const deleteRecord = (id) => {
    console.log("id: ", id)
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });
    setRecords(records.filter((el) => el._id !== id));
  }

  // This following section will display the table with the records of individuals.
  return (
    //   <table className="table table-striped" style={{ marginTop: 20 }}>
    <div style={{ margin: 20}}>
      <Typography
          sx={{ flex: '1 1 100%' }}
          variant="h6"
          id="tableTitle"
          component="div"
        >
          Nutrition
        </Typography>
      <Typography align="left">Record List</Typography>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Position</TableCell>
              <TableCell>Level</TableCell>
              <TableCell align="right">Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {records.map((currentrecord) => (
              <TableRow
                key={currentrecord._id}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
              >
                <TableCell>{currentrecord.person_level}</TableCell>
                <TableCell>{currentrecord.person_name}</TableCell>
                <TableCell>{currentrecord.person_position}</TableCell>
                <TableCell align="right">
                  <Link to={"/edit/" + currentrecord._id}>Edit</Link> |
                  <a
                    href="/"
                    onClick={() => {
                      deleteRecord(currentrecord._id);
                    }}
                  >
                    Delete
                  </a>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
